import { Component } from '@angular/core';

@Component({
  selector: 'app-pagina-no-encontrada',
  template: `
  <div class="not-found-container">
    <img src="../../../../assets/img/404.gif" alt="Imagen no encontrada" class="not-found-image">

  <p class="title-error">
    404 <br> Whooops!, That's no moon.
    <br> its an Imperial battle station</p>
  </div>
  <button type="menu" class="btn">back to home</button>
`,
styles: [`
.not-found-container {
  width: 100%;
  height: 100vh;
  display: flex;
  position:relative;
  justify-content: center;
  align-items: center;
  overflow: hidden;
}
.not-found-image {
  position: absolute;
  width: 100%;
  height: 100%;
  z-index: 1;
  left: 0px;
  top: 0px;

}
.title-error{
  color: white;
  font-size: 40px;
  z-index:2;
  text-align: center;
}
button {
  border: none;
  border-radius: 3px;
  cursor: pointer;
  transition: background-color 0.3s;
  font-size: 1rem;
  padding: 1rem 1.5rem;
  z-index: 2;
  display: flex;
  justify-content: center;

  text-align: center;


  &.btn {
    background-color: $color-tertiary;
    color: $font-color;

  }
`]
})
export class PaginaNoEncontradaComponent {

}
